import React, { useState } from 'react';
import TaskModal from './TaskModal';

const Card = ({ card, listId, handleDeleteCard, handleEditCard,setBoards, boards }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editedTask, setEditedTask] = useState({ ...card });
//console.log('hey',{card,listId, boards, editedTask})
    const handleSave = () => {
        const updatedBoards = boards.map((b) => {
            if (b.id === listId) {
              const updatedCards = b.cards.map((list) => {
                if (card.id === editedTask.id) {
                  return {
                    ...list,
                    text: editedTask.text
                  };
                }
                console.log('heye', list)
                return list; // Return the original card if not edited
              });
              return { ...b, cards: updatedCards }; // Update the cards for the current board
            }
            return b; // Return the original board if not edited
          });
        console.log('hey',{updatedBoards, boards, editedTask})
          setBoards(updatedBoards);
        setIsEditing(false);
    };
  const handleDeleteClick = () => {
    handleDeleteCard(listId, card.id);
  };


  return (
    <div className="card">
      <p>{card.text}</p>
      <button onClick={handleDeleteClick}>Delete</button>
      <button onClick={() => setIsEditing(true)}>Edit</button>
      {isEditing && (
        <TaskModal
          task={editedTask}
          setTask={setEditedTask}
          onSave={handleSave}
          onCancel={() => setIsEditing(false)}
        />
      )}
    </div>
  );
};

export default Card;
