import React, { useState } from 'react';
import Card from './Card';
import './style.css'

const List = ({ list, boards, setBoards, handleAddCard, handleDeleteCard }) => {
  const [newCardText, setNewCardText] = useState({
    text:'',
    description:'',
    deadline:''
  });

  const handleInputChange = (e) => {
    setNewCardText(e.target.value);
  };

  const handleAddClick = () => {
    if (newCardText.trim() !== '') {
      handleAddCard(list.id, newCardText);
      setNewCardText('');
    }
  };

  return (
    <div className="list">
        {!list.cards.length>0?
      <div>No task</div>:
      list.cards.map((card) => (
        <Card key={card.id} card={card} listId={list.id} handleDeleteCard={handleDeleteCard}setBoards={setBoards}boards={boards} />
      ))
}
      <div className="new-card">
        <input type="text" placeholder={"Enter list name"}value={newCardText.text} onChange={handleInputChange} />
        <input type="text" placeholder={"Enter description"}value={newCardText.description} onChange={handleInputChange} />
        <input type="text" placeholder={"Enter deadline"}value={newCardText.deadline} onChange={handleInputChange} />
        <button onClick={handleAddClick}>Add Card</button>
      </div>
    </div>
  );
};

export default List;
